import actionNames from "./actionNames";
import ticketList from '../Components/ticketList.json';

const getDefaultTickets = () => {
    const { tickets } = ticketList;
    return tickets.sort((a, b) => a.price > b.price ? 1 : -1);
}

let defaultState = {
    currency: "USD",
    tickets: [...getDefaultTickets()],
    filters: [
        { id: 1, label: 'Без пересадок', checked: true },
        { id: 2, label: '1 пересадка',  checked: true },
        { id: 3, label: '2 пересадки',  checked: true },
        { id: 4, label: '3 пересадки',  checked: true }
    ],
}

function currencyReducer (state = defaultState, action) {
    console.log('REDUCER', state, action);
    switch(action.type) {
        case actionNames.CURRENCY_CHANGE:
            const { currencyName } = action;
            let tickets = [...getDefaultTickets()];
            if (currencyName === "USD") {
                tickets = tickets.map((ticket) => ({...ticket, price: Math.trunc(ticket.price / 76)}))
            } else if (currencyName === "EUR") {
                tickets = tickets.map((ticket) => ({...ticket, price: Math.trunc(ticket.price / 86)}))
            }
            return {...state, currency: currencyName, tickets }

        case actionNames.FILTERS: 
            const { filterName } = action;
            const newFilters = state.filters.map((filter) => {
                if (filter.id === filterName) return {...filter, checked: !filter.checked};
                return {...filter}
            })
                return {...state, filters: newFilters}

        case actionNames.CHECK_ALL:
            const { toggleCheckAllName } = action;
            let allChecked = state.filters;
            if (toggleCheckAllName === 'All') {
               allChecked = allChecked.map((filter) => ({...filter, checked: !allChecked.checked}))
            }
            return {...state, filters: allChecked, tickets: [...defaultState.tickets]}

        case actionNames.MY_TICKETS:
            let someTicket = [...getDefaultTickets()];
            const ticketStops = someTicket.stops;
            const check = state.filters;
            if (check.id === 1) {
                someTicket = someTicket.filter(elem => elem.stops === 0)
            } else if (check.id === 2) {
                someTicket = someTicket.filter(elem => elem.stops === 1)
            } else if (check.id === 3) {
                someTicket = someTicket.filter(elem => elem.stops === 2)
            } else if (check.id === 4) {
                someTicket = someTicket.filter(elem => elem.stops === 3)
            }
            return {...state, tickets: someTicket}





        default:
            return {...state};  
    }
}

export default currencyReducer;

