import React from "react";
import { connect } from "react-redux";

const toggleCheckbox = (id) => ({
  type: "CHECKED",
  payload: {
    id,
  },
});

const toggleCheckAll = (checked) => ({
  type: "CHECK_ALL",
  payload: {
    checked,
  },
});

const Checkboxes = ({ state, toggleCheckbox, toggleCheckAll }) => {
  return (
    <div>
      <h2>Checkboxes:</h2>
      {state &&
        state.map(({ id, name, checked }) => (
          <div key={id}>
            <label htmlFor={name}>{name}</label>
            <input
              name={name}
              type="checkbox"
              checked={checked}
              onClick={() => toggleCheckbox(id)}
            />
          </div>
        ))}
      <label htmlFor="checkAll">Check All</label>
      <input
        name="checkAll"
        type="checkbox"
        checked={state.every((item) => item.checked)}
        onClick={({ target: { checked } }) => toggleCheckAll(checked)}
      />
    </div>
  );
};

const mapStateToProps = (state) => ({ state });

export default connect(mapStateToProps, { toggleCheckbox, toggleCheckAll })(
  Checkboxes
);

