import actionNames from "./actionNames"

const defaultState = {
    currency: "USD",
    tickets: [],
}

function currencyReducer (state = defaultState, action) {
    switch(action.type) {
        case actionNames.CURRENCY_CHANGE:
            console.log('REDUCER',action)
            if (action.currencyName === "USD") {
                return (state / 73)
            } else if (action.currencyName === "EUR") {
                return (state / 86)
            } else if (action.currencyName === "RUB") {
                return state
            }
        default:
            return state    
    }
}

export default currencyReducer;
