import React, { useState, useEffect } from 'react';
import InputField from './InputField';
import useValidator from './validator';



const fieldEmpty  = 'Поле должно быть заполненым';

const defaultInputs = {
    email: {id: 1, error: fieldEmpty, placeholder: 'Enter your email', name: 'email', value: '', dirty: false},
    phone: {id: 2, error: fieldEmpty, placeholder: 'Enter your phone', name: 'phone', value: '', dirty: false},
    firstname: {id: 3, error: fieldEmpty, placeholder: 'Enter your firstname', name: 'firstname', value: '', dirty: false},
    secondname: {id: 4, error: fieldEmpty, placeholder: 'Enter your secondname', name: 'secondname', value: '', dirty: false},
    passportnumber: {id: 5, error: fieldEmpty, placeholder: 'Enter your passportnumber', name: 'passportnumber', value: '', dirty: false},
}

function ModalWindow ({setActive, setActiveSuccess}) {
    const { validateHandler } = useValidator();

    const [formState, setFormState] = useState(defaultInputs);
    const [valid, setValid] = useState(false)

    useEffect(() => {
        if(formState) {
            setValid(false)
        }else{
            setValid(true)
        }

    },[formState])

    const onChange = (event) => {
        const {value, name} = event.target;
        const currentField = formState[event.target.name];
        if (!currentField) return;
        const error = validateHandler(name, value)
        setFormState({...formState, ...{[name]: {...currentField, value, error}}})
    }

    const onBlur = (event) => {
        const {name} = event.target;
        const currentField = formState[event.target.name];
        setFormState({...formState, ...{[name]: {...currentField, dirty: true}}})
    }

    return (
        <>
        <div className='modal active' onClick={() => setActive(false)}>
            <div className='modal__content active' onClick={e => e.stopPropagation()}>
                <div className='formModal'>
                <form className='formValid' onSubmit={(e) => {
                    e.preventDefault();
                    setActive(false);
                }}
                >
                    <h1 className='order'>Order window</h1>
                    {Object.keys(formState).map((fieldName) => <InputField {...formState[fieldName]} onChange={onChange} onBlur={onBlur}/>)}
                    <button disabled={!valid} type='submit' className='btnOrder' onClick={() => setActiveSuccess(true)}>Order</button>

                </form>
                </div>
            </div>
        </div>
        </>
    )
}

export default ModalWindow;
