import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { currencyChangeAction, filterActions } from '../Redux/actions';
import FilterItem from './FilterItem';
import FilterAll from './FilterAll'
import {useState} from 'react'

function Сurrency({id}) {

    const [colorBtn_1,setColor_1]= useState(false);
    const [colorBtn_2,setColor_2]= useState(false);
    const [colorBtn_3,setColor_3]= useState(false);

    function onClick () {
        if (colorBtn_1 === true) {
            style: {
                color: "white";
                backgroundColor: "rgb(0, 110, 255)"
            }
        }
    }



    const filters = useSelector(state => state.root.filters);
    const dispatch = useDispatch();
    return (
        <div className="currencyStyle" key={id}>
            <span>ВАЛЮТА</span>
            <div className="btnCur">
                <button className="btn_1 btn_color" 
                    onClick={() => {dispatch(currencyChangeAction('RUB')); 
                    setColor_1(true)}} style={{backgroundColor:colorBtn_1==true?"rgb(0, 110, 255)":""}} 
                >
                    RUB
                </button>



                <button className="btn_2 btn_color" onClick={() => { dispatch(currencyChangeAction('USD')); setColor_2(true)}} style={{backgroundColor:colorBtn_2==true?"rgb(0, 110, 255)":""}}>USD</button>
                <button className="btn_3 btn_color" onClick={() => { dispatch(currencyChangeAction('EUR')); setColor_3(true)}} style={{backgroundColor:colorBtn_3==true?"rgb(0, 110, 255)":""}}>EUR</button>
            </div>
            <ul className="transfer">
                <span >КОЛИЧЕСТВО ПЕРЕСАДОК</span>
                <FilterAll checked={filters.every(filter => filter.checked)}/>
                {filters.map((filter) => (
                    <FilterItem key={id} {...filter} onClick={(id) => dispatch(filterActions(id))} />
                ))}
            </ul>
        </div>
    )
}

export default Сurrency;
