import React from 'react';
import useValidator from './validator';


function InputField ({name, placeholder, onChange, error, value}) {

    const { state, stateDirty, stateError, emailHandled, phoneHandled, firstnameHandled, secondnameHandled, passportnumberHandled, blurHandler } = useValidator()

    return (
        <>
            <input name={name} type='text' placeholder={placeholder} 
                   onChange={onChange} 
                   value={value} 
                   onBlur={(e) => blurHandler(e)}  
                   className={`${stateDirty.email && stateError.emailError ? 'inputValidError' : 'inputValid'}`}
            />
            {(stateDirty.email && stateError.emailError) && <div className="divError" style={{color: 'red'}}>{stateError.emailError}</div>}
        </>
    )
}


export default InputField;
