import React from 'react';
import useValidator from './validator';


function InputField ({name, placeholder, onChange, error, value, dirty, onBlur}) {

    const { state, stateDirty, stateError, emailHandled, phoneHandled, firstnameHandled, secondnameHandled, passportnumberHandled, blurHandler } = useValidator()

    return (
        <>
            <input 
                name={name} 
                type='text' 
                placeholder={placeholder} 
                onChange={onChange} 
                value={value} 
                onBlur={onBlur}  
                className={`${dirty && error ? 'inputValidError' : 'inputValid'}`}
            />
            {(dirty && error) && <div className="divError" style={{color: 'red'}}>{error}</div>}
        </>
    )
}


export default InputField;
