export function createStore (currencyReducer, initialState) {
    let state = currencyReducer(initialState, {type: "__INIT__"})
    const subscribers = []

    return {
        dispatch(action) {
            state = currencyReducer(state, action)
            subscribers.forEach(sub => sub())
        },
        subscribe(callback) {
            subscribers.push(callback)
        },
        getState() {
            return state
        }
    }
}
