import React from "react";
import { createStore } from "redux";
import { Provider } from "react-redux";
import CheckBoxes from "./Redux/currencyReducer";

const initialState = [
  { id: "1", name: "name1", checked: false },
  { id: "2", name: "name2", checked: false },
  { id: "3", name: "name3", checked: true },
  { id: "4", name: "name4", checked: false },
];

const reducer = (state = initialState, { type, payload }) => {
  switch (type) {
    case "CHECKED":
      return state.map((item) =>
        item.id === payload.id ? { ...item, checked: !item.checked } : item
      );
    case "CHECK_ALL":
      return state.map((item) => ({ ...item, checked: payload.checked }));    
    default:
      return state;
  }
};

const store = createStore(reducer);

const App = () => {
  return (
    <Provider store={store}>
      <CheckBoxes/>
    </Provider>
  );
};

