import actionNames from "./actionNames";
import ticketList from '../Components/ticketList.json';

const getDefaultTickets = () => {
    const { tickets } = ticketList;
    return tickets.sort((a, b) => a.price > b.price ? 1 : -1);
}

let defaultState = {
    currency: "USD",
    tickets: [...getDefaultTickets()],
    filters: [
        // { title: 0, completed: false, id: 1 },
        // { title: 1, completed: false, id: 2 },
        // { title: 2, completed: false, id: 3 },
        // { title: 3, completed: false, id: 4 },
    ]
}

function currencyReducer (state = defaultState, action, {id, filters}) {
    console.log('REDUCER', state, action);
    switch(action.type) {
        case actionNames.CURRENCY_CHANGE:
            const { currencyName } = action;
            let tickets = [...getDefaultTickets()];
            if (currencyName === "USD") {
                tickets = tickets.map((ticket) => ({...ticket, price: Math.trunc(ticket.price / 76)}))
            } else if (currencyName === "EUR") {
                tickets = tickets.map((ticket) => ({...ticket, price: Math.trunc(ticket.price / 86)}))
            }
            return {...state, currency: currencyName, tickets }

        case actionNames.FILTERS: 
            const { filterName } = action;
            const newFilters = [...state.filters, filterName];

            // filters = state.filters.map(item => {
            //     if(item.id === id) {
            //         item.completed = !item.completed
            //     }
            //     return item
            // })


            // const ticketsFilter = state.tickets.filter((ticket) => {
            //     return newFilters.some(filter => filter === ticket.stops)
            // });



            // if (filterName === "NON STOPS") {
            //     tickets = tickets.filter((ticket) => ({...ticket, stops: ticket.stops === 0}))
            // } else if (filterName === "1 STOP") {
            //     tickets = tickets.filter((ticket) => ({...ticket, stops: ticket.stops === 1}))
            // } else if (filterName === "2 STOPS") {
            //     tickets = tickets.filter((ticket) => ({...ticket, stops: ticket.stops === 2}))
            // } else if (filterName === "3 STOPS") {
            //     tickets = tickets.filter((ticket) => ({...ticket, stops: ticket.stops === 3}))
            // }


            return {...state, filters: newFilters}

        default:
            return {...state};  
    }
}

export default currencyReducer;
